import React from 'react';

function AboutAllergies() {
  return (
    <div className="about-allergies">
      <h2>About Food Allergies</h2>
      <p>Learn about the major allergens and how to stay safe:</p>
      <ul>
        <li><strong>Milk:</strong> Found in dairy products. Watch for labels!</li>
        <li><strong>Eggs:</strong> Common in baked goods and more.</li>
        <li><strong>Fish:</strong> Check for fish symbols in seafood.</li>
        <li><strong>Shellfish:</strong> Includes shrimp, crab, lobster.</li>
        <li><strong>Tree Nuts:</strong> Almonds, walnuts, cashews, etc.</li>
        <li><strong>Peanuts:</strong> One of the most common allergens.</li>
        <li><strong>Wheat:</strong> Found in bread, pasta, and baked treats.</li>
        <li><strong>Soy:</strong> Present in tofu, soy milk, and processed foods.</li>
      </ul>
    </div>
  );
}

export default AboutAllergies;
