// /src/games/SortingPlateGame.js
import React, { useState } from 'react';

const foodItems = [
  { id: 1, name: 'Milk Carton', allergens: ['milk'] },
  { id: 2, name: 'Peanut Butter', allergens: ['peanuts'] },
  { id: 3, name: 'Apple', allergens: [] },
  { id: 4, name: 'Bread', allergens: ['wheat'] },
  { id: 5, name: 'Egg Omelette', allergens: ['eggs'] },
  { id: 6, name: 'Carrot', allergens: [] },
];

function SortingPlateGame() {
  const [safePlate, setSafePlate] = useState([]);
  const [notSafePlate, setNotSafePlate] = useState([]);
  const [message, setMessage] = useState('');
  // In this example, the level focuses on avoiding peanuts.
  const allergenToAvoid = 'peanuts';

  const handleFoodClick = (item) => {
    if (item.allergens.includes(allergenToAvoid)) {
      setNotSafePlate([...notSafePlate, item]);
      setMessage(`Oops! ${item.name} contains ${allergenToAvoid}.`);
    } else {
      setSafePlate([...safePlate, item]);
      setMessage(`Great! ${item.name} is safe.`);
    }
  };

  return (
    <div className="sorting-plate-game">
      <h2>Sorting Plate Game</h2>
      <p>Click a food item to place it on the right plate.</p>
      <div className="food-items">
        {foodItems.map((item) => (
          <button
            key={item.id}
            onClick={() => handleFoodClick(item)}
            className="food-button"
          >
            {item.name}
          </button>
        ))}
      </div>
      <div className="plates">
        <div className="plate safe-plate">
          <h3>Safe Plate</h3>
          <ul>
            {safePlate.map((item) => (
              <li key={item.id}>{item.name}</li>
            ))}
          </ul>
        </div>
        <div className="plate not-safe-plate">
          <h3>Not Safe Plate</h3>
          <ul>
            {notSafePlate.map((item) => (
              <li key={item.id}>{item.name}</li>
            ))}
          </ul>
        </div>
      </div>
      {message && <p className="message">{message}</p>}
    </div>
  );
}

export default SortingPlateGame;
