// /src/pages/GamesPage.js
import React from 'react';
import { Link, useLocation } from 'react-router-dom';

function GamesPage() {
  const location = useLocation();
  const ageGroup = location.state?.ageGroup || 'all ages';

  return (
    <div className="games-page">
      <h2>Games for {ageGroup}</h2>
      <p>Select a game to learn about food safety:</p>
      <div className="game-cards">
        <Link to="/games/sorting" className="game-card">Sorting Plate Game</Link>
        <Link to="/games/label" className="game-card">Label Reading Game</Link>
        {/* Additional games can be added here */}
      </div>
    </div>
  );
}

export default GamesPage;
