import React, { useContext, useState } from 'react';
import './SortingPlateGame.css';
import { UserSettingsContext } from '../UserSettingsContext';
import PlateDisplay from '../components/PlateDisplay';
import '../components/PlateDisplay.css';

const allergenEmojis = {
  milk: 'ü•õ',
  peanuts: 'ü•ú',
  eggs: 'ü•ö',
  wheat: 'üåæ',
  fish: 'üêü',
  shellfish: 'ü¶ê',
  soy: 'üå±',
  'tree nuts': 'üå∞'
};

const foodItems = [
  { id: 1, name: 'Milk Carton', allergens: ['milk'], emoji: 'ü•õ' },
  { id: 2, name: 'Peanut Butter', allergens: ['peanuts'], emoji: 'ü•ú' },
  { id: 3, name: 'Apple', allergens: [], emoji: 'üçé' },
  { id: 4, name: 'Bread', allergens: ['wheat'], emoji: 'üçû' },
  { id: 5, name: 'Egg Omelette', allergens: ['eggs'], emoji: 'üç≥' },
  { id: 6, name: 'Carrot', allergens: [], emoji: 'ü•ï' },
  { id: 7, name: 'Fish Stick', allergens: ['fish'], emoji: 'üêü' },
  { id: 8, name: 'Yogurt', allergens: ['milk'], emoji: 'ü•õ' }
];

function SortingPlateGame() {
  const { allergen, ageGroup } = useContext(UserSettingsContext);
  const [safePlate, setSafePlate] = useState([]);
  const [message, setMessage] = useState('');

  const handleFoodClick = (item) => {
    if (item.allergens.includes(allergen)) {
      setMessage(`Oops! ${item.emoji} ${item.name} contains ${allergenEmojis[allergen]}. Please avoid it.`);
    } else {
      // If the plate is full (simulate a boundary), we can show a warning.
      if (safePlate.length >= 12) {
        setMessage("Your plate is full! Try removing an item or finish the game.");
        return;
      }
      setSafePlate((prev) => [...prev, item]);
      setMessage(`Great job! ${item.emoji} ${item.name} is safe üëç`);
    }
  };

  return (
    <div className="sorting-plate-game">
      <h2>Sorting Plate Game</h2>
      <p className="instructions">
        Tap a food item to add it to your safe plate. Avoid foods with {allergenEmojis[allergen]}!
      </p>
      <div className="food-items">
        {foodItems.map((item) => (
          <button key={item.id} onClick={() => handleFoodClick(item)} className="food-button">
            <span className="food-emoji">{item.emoji}</span>
            <span className="food-name">{item.name}</span>
          </button>
        ))}
      </div>
      <div className="plate safe-plate">
        <h3>Safe Plate üòÉ</h3>
        <PlateDisplay items={safePlate} />
      </div>
      {message && <p className="message">{message}</p>}
    </div>
  );
}

export default SortingPlateGame;
