import React from 'react';
import { Link, useLocation } from 'react-router-dom';

function GamesPage() {
  const location = useLocation();
  const ageGroup = location.state?.ageGroup || 'all ages';

  return (
    <div className="games-page" style={{ textAlign: 'center', padding: '40px 20px' }}>
      <h2 style={{ fontSize: '2em', marginBottom: '20px', color: '#4a4a4a' }}>Games for {ageGroup}</h2>
      <p style={{ fontSize: '1.2em', marginBottom: '30px' }}>
        Choose a game to learn about food safety in a fun, interactive way.
      </p>
      <div className="game-cards" style={{ display: 'flex', justifyContent: 'center', gap: '20px', flexWrap: 'wrap' }}>
        <Link to="/games/sorting" className="game-card" style={{
          backgroundColor: '#a0d468',
          padding: '20px 30px',
          borderRadius: '10px',
          textDecoration: 'none',
          color: '#fff',
          fontWeight: '600',
          fontSize: '1.1em',
          boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
          transition: 'transform 0.2s ease'
        }}>
          Sorting Plate Game
        </Link>
        <Link to="/games/label" className="game-card" style={{
          backgroundColor: '#4fc1e9',
          padding: '20px 30px',
          borderRadius: '10px',
          textDecoration: 'none',
          color: '#fff',
          fontWeight: '600',
          fontSize: '1.1em',
          boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
          transition: 'transform 0.2s ease'
        }}>
          Label Reading Game
        </Link>
      </div>
    </div>
  );
}

export default GamesPage;
