import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import InfoModal from '../components/InfoModal';

function HomePage() {
  const [modalOpen, setModalOpen] = useState(false);

  return (
    <div className="home-page" style={{ textAlign: 'center', padding: '60px 20px' }}>
      <h1>Welcome to SafePlate</h1>
      <p style={{ fontSize: '1.3em', marginBottom: '30px' }}>
        Empowering families to learn about food allergies and make safe choices.
      </p>
      <Link to="/age-selection" className="start-button">
        Get Started
      </Link>
      <div style={{ marginTop: '40px' }}>
        <button onClick={() => setModalOpen(true)} className="learn-more-button">
          Learn More About SafePlate
        </button>
      </div>
      <InfoModal 
        isOpen={modalOpen} 
        onClose={() => setModalOpen(false)} 
        title="About SafePlate"
      >
        <p>
          SafePlate is designed to teach children and parents about food allergies. Learn how to identify allergens, handle emergencies, and make informed choices at home, school, or while dining out.
        </p>
      </InfoModal>
    </div>
  );
}

export default HomePage;
