import React, { useState } from 'react';
import './SortingPlateGame.css';

const allergenEmojis = {
  milk: 'ü•õ',
  peanuts: 'ü•ú',
  eggs: 'ü•ö',
  wheat: 'üåæ',
  fish: 'üêü',
  shellfish: 'ü¶ê',
  soy: 'üå±',
  'tree nuts': 'üå∞'
};

const foodItems = [
  { id: 1, name: 'Milk Carton', allergens: ['milk'], emoji: 'ü•õ' },
  { id: 2, name: 'Peanut Butter', allergens: ['peanuts'], emoji: 'ü•ú' },
  { id: 3, name: 'Apple', allergens: [], emoji: 'üçé' },
  { id: 4, name: 'Bread', allergens: ['wheat'], emoji: 'üçû' },
  { id: 5, name: 'Egg Omelette', allergens: ['eggs'], emoji: 'üç≥' },
  { id: 6, name: 'Carrot', allergens: [], emoji: 'ü•ï' }
];

function SortingPlateGame() {
  const [allergenSelected, setAllergenSelected] = useState(null);
  const [safePlate, setSafePlate] = useState([]);
  const [message, setMessage] = useState('');

  const allergenOptions = [
    { id: 'milk', label: 'Milk', emoji: allergenEmojis['milk'] },
    { id: 'peanuts', label: 'Peanuts', emoji: allergenEmojis['peanuts'] },
    { id: 'eggs', label: 'Eggs', emoji: allergenEmojis['eggs'] },
    { id: 'wheat', label: 'Wheat', emoji: allergenEmojis['wheat'] }
    // Add more options as needed
  ];

  const handleAllergenSelection = (option) => {
    setAllergenSelected(option.id);
    setMessage(`You selected ${option.label} ${option.emoji}. Avoid foods with this allergen!`);
  };

  const handleFoodClick = (item) => {
    if (item.allergens.includes(allergenSelected)) {
      setMessage(`Oh no! ${item.emoji} ${item.name} contains ${allergenEmojis[allergenSelected]}. Please avoid it.`);
    } else {
      setSafePlate([...safePlate, item]);
      setMessage(`Great job! ${item.emoji} ${item.name} is safe üëç`);
    }
  };

  // If allergen hasn't been selected, show the selection interface
  if (!allergenSelected) {
    return (
      <div className="sorting-plate-game">
        <h2>Choose Your Allergen</h2>
        <p>Please select the allergen you need to avoid:</p>
        <div className="allergen-selection">
          {allergenOptions.map((option) => (
            <button
              key={option.id}
              className="allergen-button"
              onClick={() => handleAllergenSelection(option)}
            >
              <span className="allergen-emoji">{option.emoji}</span>
              <span className="allergen-label">{option.label}</span>
            </button>
          ))}
        </div>
      </div>
    );
  }

  // Once allergen is selected, show the game interface
  return (
    <div className="sorting-plate-game">
      <h2>Sorting Plate Game</h2>
      <p className="instructions">
        Tap a food item to add it to your safe plate. Avoid foods with {allergenEmojis[allergenSelected]}!
      </p>
      <div className="food-items">
        {foodItems.map((item) => (
          <button key={item.id} onClick={() => handleFoodClick(item)} className="food-button">
            <span className="food-emoji">{item.emoji}</span>
            <span className="food-name">{item.name}</span>
          </button>
        ))}
      </div>
      <div className="plate safe-plate">
        <h3>Safe Plate üòÉ</h3>
        <ul>
          {safePlate.map((item) => (
            <li key={item.id}>
              <span className="food-emoji">{item.emoji}</span> {item.name}
            </li>
          ))}
        </ul>
      </div>
      {message && <p className="message">{message}</p>}
    </div>
  );
}

export default SortingPlateGame;
