import React, { useContext, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { UserSettingsContext } from '../UserSettingsContext';

function AgeAllergenSelection() {
  const { setAgeGroup, setAllergen } = useContext(UserSettingsContext);
  const navigate = useNavigate();

  const ageOptions = [
    { id: '2-4', label: 'Ages 2-4' },
    { id: '5-7', label: 'Ages 5-7' },
    { id: '8-12', label: 'Ages 8-12' },
    { id: 'teens', label: 'Teens' }
  ];

  const allergenOptions = [
    { id: 'milk', label: 'Milk', emoji: 'ðŸ¥›' },
    { id: 'peanuts', label: 'Peanuts', emoji: 'ðŸ¥œ' },
    { id: 'eggs', label: 'Eggs', emoji: 'ðŸ¥š' },
    { id: 'wheat', label: 'Wheat', emoji: 'ðŸŒ¾' }
  ];

  const [selectedAge, setSelectedAge] = useState(null);
  const [selectedAllergen, setSelectedAllergen] = useState(null);

  const handleSelection = () => {
    setAgeGroup(selectedAge);
    setAllergen(selectedAllergen);
    navigate('/games');
  };

  return (
    <div className="age-allergen-selection" style={{ textAlign: 'center', padding: '40px 20px' }}>
      <h2>Select Your Age Group and Allergen</h2>
      <div className="selection-section">
        <h3>Choose your age:</h3>
        <div className="age-options" style={{ display: 'flex', justifyContent: 'center', gap: '20px', flexWrap: 'wrap' }}>
          {ageOptions.map((age) => (
            <button 
              key={age.id} 
              className={`selection-button ${selectedAge === age.id ? 'selected' : ''}`}
              onClick={() => setSelectedAge(age.id)}
            >
              {age.label}
            </button>
          ))}
        </div>
      </div>
      <div className="selection-section" style={{ marginTop: '30px' }}>
        <h3>Choose your allergen:</h3>
        <div className="allergen-options" style={{ display: 'flex', justifyContent: 'center', gap: '20px', flexWrap: 'wrap' }}>
          {allergenOptions.map((allergen) => (
            <button 
              key={allergen.id} 
              className={`selection-button ${selectedAllergen === allergen.id ? 'selected' : ''}`}
              onClick={() => setSelectedAllergen(allergen.id)}
            >
              <span className="allergen-emoji">{allergen.emoji}</span> {allergen.label}
            </button>
          ))}
        </div>
      </div>
      {selectedAge && selectedAllergen && (
        <div style={{ marginTop: '40px' }}>
          <button className="start-game-button" onClick={handleSelection}>
            Start Your SafePlate Experience
          </button>
        </div>
      )}
    </div>
  );
}

export default AgeAllergenSelection;
