import React, { useContext, useState } from 'react';
import { UserSettingsContext } from '../UserSettingsContext';
import './SortingPlateGame.css';

// Example allergen emojis
const allergenEmojis = {
  milk: '🥛',
  peanuts: '🥜',
  eggs: '🥚',
  wheat: '🌾',
  fish: '🐟',
  shellfish: '🦐',
  soy: '🌱',
  'tree nuts': '🌰'
};

// Sample food items with icons and allergens
const foodItems = [
  { id: 1, name: 'Milk Carton', allergens: ['milk'], emoji: '🥛' },
  { id: 2, name: 'Peanut Butter', allergens: ['peanuts'], emoji: '🥜' },
  { id: 3, name: 'Apple', allergens: [], emoji: '🍎' },
  { id: 4, name: 'Bread', allergens: ['wheat'], emoji: '🍞' },
  { id: 5, name: 'Egg Omelette', allergens: ['eggs'], emoji: '🍳' },
  { id: 6, name: 'Carrot', allergens: [], emoji: '🥕' },
  { id: 7, name: 'Fish Stick', allergens: ['fish'], emoji: '🐟' },
  { id: 8, name: 'Yogurt', allergens: ['milk'], emoji: '🥛' }
];

function SortingPlateGame() {
  // Retrieve allergen setting from global context
  const { allergen } = useContext(UserSettingsContext) || {};
  const [safePlate, setSafePlate] = useState([]);
  const [message, setMessage] = useState('');

  const handleFoodClick = (item) => {
    // If this item has the chosen allergen, show a warning
    if (allergen && item.allergens.includes(allergen)) {
      setMessage(`Oops! ${item.emoji} ${item.name} contains ${allergenEmojis[allergen]}. Please avoid it.`);
    } else {
      // Optionally limit how many items can fit on the plate
      if (safePlate.length >= 12) {
        setMessage('Your plate is full! Try removing an item or finishing the game.');
        return;
      }
      setSafePlate([...safePlate, item]);
      setMessage(`Great job! ${item.emoji} ${item.name} is safe 👍`);
    }
  };

  // If no allergen is selected, just default to "peanuts" or show a fallback
  const allergenSymbol = allergenEmojis[allergen] || allergenEmojis['peanuts'];

  // Optional function to remove an item from the plate if user wants
  const removeItem = (index) => {
    const updated = [...safePlate];
    updated.splice(index, 1);
    setSafePlate(updated);
  };

  return (
    <div className="sorting-plate-container">
      <h2 className="game-title">Sorting Plate Game</h2>
      <p className="instructions">
        Tap a food item to add it to your plate. Avoid foods with {allergenSymbol}!
      </p>

      <div className="game-layout">
        {/* LEFT COLUMN: Food items in a grid */}
        <div className="food-items-grid">
          {foodItems.map((item) => (
            <button
              key={item.id}
              onClick={() => handleFoodClick(item)}
              className="food-button"
            >
              <span className="food-emoji">{item.emoji}</span>
              <span className="food-name">{item.name}</span>
            </button>
          ))}
        </div>

        {/* RIGHT COLUMN: Plate display */}
        <div className="plate-area">
          <div className="plate-wrapper">
            <div className="plate">
              {/* Items placed on plate */}
              <div className="plate-grid">
                {safePlate.map((item, index) => (
                  <div key={index} className="plate-item">
                    <span className="plate-item-emoji">{item.emoji}</span>
                    <span className="plate-item-name">{item.name}</span>
                    {/* Optional remove button */}
                    <button
                      className="remove-button"
                      onClick={() => removeItem(index)}
                    >
                      ✖
                    </button>
                  </div>
                ))}
              </div>
            </div>
          </div>
          {message && <p className="plate-message">{message}</p>}
        </div>
      </div>
    </div>
  );
}

export default SortingPlateGame;
